/* 6. Search modal with Pagefind */
(function(){var m=document.getElementById("search-modal");if(!m)return;var sr=m.getAttribute("data-show-sub-results")!=="false",si=m.getAttribute("data-show-images")!=="false",ph=m.getAttribute("data-placeholder")||"Search...",nr=m.getAttribute("data-no-results")||"No results found",loaded=false,hlIdx=-1;function getR(){return Array.from(document.querySelectorAll("#search .pagefind-ui__result"))}function clr(){getR().forEach(function(r){r.classList.remove("search-highlight")});hlIdx=-1}function setH(idx){var r=getR();if(!r.length)return;clr();if(idx<0)idx=r.length-1;if(idx>=r.length)idx=0;hlIdx=idx;r[hlIdx].classList.add("search-highlight");r[hlIdx].scrollIntoView({block:"nearest"})}function open(){if(!loaded){loaded=true;var l=document.createElement("link");l.rel="stylesheet";l.href="/pagefind/pagefind-ui.css";document.head.appendChild(l);var s=document.createElement("script");s.src="/pagefind/pagefind-ui.js";s.onload=function(){try{new PagefindUI({element:"#search",showSubResults:sr,showImages:si,translations:{placeholder:ph,zero_results:nr}})}catch(e){document.getElementById("search").innerHTML='<p class="p-4 text-sm text-slate-500">Search is not available.</p>'}var inp=document.querySelector("#search .pagefind-ui__search-input");if(inp){inp.id="search-input";inp.name="search";inp.addEventListener("input",function(){clr()})}setTimeout(function(){var i=m.querySelector(".pagefind-ui__search-input");if(i)i.focus()},100)};s.onerror=function(){document.getElementById("search").innerHTML='<p class="p-4 text-sm text-slate-500">Search is not available.</p>'};document.head.appendChild(s)}else{setTimeout(function(){var i=m.querySelector(".pagefind-ui__search-input");if(i){i.focus();i.select()}},50)}m.classList.remove("hidden");m.setAttribute("aria-hidden","false");document.body.style.overflow="hidden"}function close(){m.classList.add("hidden");m.setAttribute("aria-hidden","true");document.body.style.overflow="";clr();var t=document.getElementById("search-trigger");if(t)t.focus()}document.addEventListener("click",function(e){if(e.target&&e.target.id==="search-trigger"||e.target.closest&&e.target.closest("#search-trigger"))open();if(e.target&&e.target.id==="search-backdrop")close()});document.addEventListener("keydown",function(e){if((e.metaKey||e.ctrlKey)&&e.key==="k"){e.preventDefault();if(m.classList.contains("hidden"))open();else close()}if(m.classList.contains("hidden"))return;if(e.key==="Escape"){e.stopPropagation();close();return}if(e.key==="ArrowDown"){e.preventDefault();setH(hlIdx+1)}else if(e.key==="ArrowUp"){e.preventDefault();setH(hlIdx-1)}else if(e.key==="Enter"&&hlIdx>=0){var r=getR();if(r[hlIdx]){var a=r[hlIdx].querySelector("a");if(a){e.preventDefault();a.click()}}}})})();
